insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (1, 2, 'ERIC2020', 35000.00, 0, 'asrock-eric2020.jpg', 'Las placas base de la serie FM2+ de ASrock están basadas en el chipset A88X de AMD, incluyen Socket FM2+ para APU y están diseñadas para obtener el máximo rendimiento de las nuevas APU Kaveri de AMD.', '', 'destacado', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (2, 3, 'GT332', 35000.00, 0, 'nvidia-gt332.jpg', 'Nvidia es el fabricante líder de placas de video, su calidad asegura una experiencia positiva en el desarrollo del motor gráfico de tu computadora. Además, sus procesadores usan tecnología de punta para que puedas disfrutar de un producto veloz y duradero.', '', 'in-sale', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (2, 3, 'GT440', 28500.00, 0, 'nvidia-gt440.jpg', 'En el menor tiempo posible con una velocidad de memoria de 1600 MHz los datos del procesador central se van a traducir en información comprensible en tan solo un abrir y cerrar de ojos. Tu placa de video trabajará con rapidez para maximizar el tiempo real.', '', 'in-sale', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (2, 4, 'GT456', 20000.00, 0, 'dell-gt456.jpg', 'Disfrutá de todas las cualidades que el monitor Dell P2018H tiene para ofrecerte. Percibí las imágenes de una manera completamente diferente y complementa cualquier espacio ya sea en tu casa u oficina.', '', 'in-sale', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
