insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (4, 6, 'I6LK2', 26000.00, 0, 'intel-i5lk2.jpg', 'Productividad y entretenimiento, todo disponible en computadoras de escritorio. La superioridad tecnológica de Intel es un beneficio para todo tipo de profesionales. Asegura el mejor rendimiento de las aplicaciones, de la transferencia de datos y la conexión con otros elementos tecnológicos.', '', 'in-sale', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (4, 6, 'I711', 32000.00, 0, 'intel-i711.jpg', 'Núcleos: el corazón del procesador dentro de este producto vas a encontrar los núcleos, que son los encargados de ejecutar las instrucciones y actividades que le asignás a tu dispositivo. Estos están en relación directa con dos elemetos: los hilos y el modelo, por lo que a la hora de elegir un procesador es importante que valores los tres en su conjunto.', '', '', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
insert into products (category_id, brand_id, model, price, discount, images, description, features, status, created_at, updated_at) values (4, 7, 'I7-I', 34000.00, 0, 'amd-19-i.jpg', 'Clave en el rendimiento de computadoras de escritorio, ya no tenés que pensar en cómo distribuir el tiempo y acciones: ahora todas las tareas en simultáneo son posibles. AMD cuenta con un catálogo de productos que se adaptan a los requerimientos de todo tipo de usuarios: juegos en línea, edición a gran escala, contenido en múltiples plataformas y más.', '', '', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
